const {Vector2} = require("blockade/vectors");

class Entity {
	static object = "Entity";
	static type = "Base";

	/**
	 * Create a new Entity instance
	 * @param {Blockade} game
	 * @param {Vector2} position
	 */
	constructor(game, position=new Vector2()) {
		this.game = game;
		this.position = position;
		this.color = 0;
		this.changed = false;
	}

	/**
	 * Tick this Entity
	 */
	tick() {

	}

	/**
	 * Update this Entity
	 * @returns {boolean} if the entity has changed
	 */
	update() {
		this.changed = false;
		return this.changed;
	}
}

module.exports = {Entity};