const {Entity} = require("blockade/entity/entity");

class Plate extends Entity {
	/**
	 * Create new Plate Instance
	 * @param {Blockade} game
	 * @param {Vector2} position
	 * @param {PlateType} type
	 */
	constructor(game, position, type) {
		super(game, position);
		this.type = type;
		this.pressed = false;
	}

	update() {
		super.update();
		this.type.update(this);
		return this.changed;
	}
}

module.exports = {Plate};