const {Entity} = require("blockade/entity/entity");

class Tile extends Entity {
	/**
	 * Create new Tile Instance
	 * @param {Blockade} game
	 * @param {Vector2} position
	 * @param {TileType} type
	 */
	constructor(game, position, type) {
		super(game, position);
		this.type = type;
	}
}

module.exports = {Tile};