const {Tile} = require("blockade/tiles/tile");
const {Standard} = require("blockade/tiles/standard");
const {Sticky} = require("blockade/tiles/sticky");

const tiles = {Tile, Standard, Sticky};

function newTile(type, game, position) {
	if (type === "sticky") return new Sticky(game, position);
	return new Standard(game, position);
}

module.exports = {Tile, tiles, newTile};