const {Tile} = require("blockade/tiles/tile");
const {Vector2} = require("blockade/vectors");

class Sticky extends Tile {
	static type = "sticky";

	update() {
		super.update();
		let block = this.game.getBlock(this.position);
		if (block === undefined) return false;
		block.velocity = Vector2.zero;
		return this.changed;
	}
}

module.exports = {Sticky};